#include <openssl/rsa.h>
#include <openssl/err.h>
#include <string.h>
#include <stdlib.h>
#include "jmauth.h"
#include "jmdiskinfowin32.h"
#include "cyo/CyoEncode.h"
#include "cyo/CyoDecode.h"

#ifdef G_OS_WIN32
#define jm_disk_info_init jm_disk_info_init_win32
#define jm_disk_info_destroy jm_disk_info_destroy_win32
#define jm_disk_info_load jm_disk_info_load_win32
#define jm_disk_info_serial_number jm_disk_info_serial_number_win32
#else
#endif

// N: 
// E: 5847329
// D: 
// the encryption 64base text is:
// 1. ID code generate with Hardware SN \n
// 2. Commbox Customer ID CAEN/CACN/XXXXXXXXXXX \n
// 3. First Regisiter Time \n
// 4. Expired Update Time \n
// 5. The database password \n
// 6. The language \n
// Only the ID Code get from client computer;
#define CRYPT_MESSAGE_LINES 6
static const char *_jm_auth_the_n = "";
static const char *_jm_auth_the_e = "520867428764322287956";
static const char *_jm_auth_the_d = "";

static RSA *_jm_auth_rsa = NULL;
GString *_jm_auth_dat_path = NULL;

void jm_auth_init(void)
{
    GString *n = g_string_new("");
    GString *e = g_string_new("");
    GString *d = g_string_new("");

    glong i;
    glong length;

    jm_disk_info_init();

    length = g_utf8_strlen(_jm_auth_the_n, -1);
    for (i = 0; i < length; i += 2)
    {
        g_string_append_c(n, _jm_auth_the_n[i]);
    }

    length = g_utf8_strlen(_jm_auth_the_e, -1);
    for (i = 0; i < length; i += 3)
    {
        g_string_append_c(e, _jm_auth_the_e[i]);
    }

    length = g_utf8_strlen(_jm_auth_the_d, -1);
    for (i = 0; i < length; i += 4)
    {
        g_string_append_c(d, _jm_auth_the_d[i]);
    }

    _jm_auth_rsa = RSA_new();

    _jm_auth_rsa->n = BN_new();
    _jm_auth_rsa->e = BN_new();
    _jm_auth_rsa->d = BN_new();

    BN_dec2bn(&(_jm_auth_rsa->n), n->str);
    BN_dec2bn(&(_jm_auth_rsa->e), e->str);
    BN_dec2bn(&(_jm_auth_rsa->d), d->str);

    g_string_free(n, TRUE);
    g_string_free(e, TRUE);
    g_string_free(d, TRUE);

    _jm_auth_dat_path = g_string_new("");
}

void jm_auth_destroy(void)
{
    jm_disk_info_destroy();
    g_string_free(_jm_auth_dat_path, TRUE);
    RSA_free(_jm_auth_rsa);
}

void jm_auth_set_dat_path(const gchar *path)
{
    _jm_auth_dat_path = g_string_assign(_jm_auth_dat_path, path);
    _jm_auth_dat_path = g_string_append(_jm_auth_dat_path, "demo.dat");
}

void jm_auth_save_reg(const gchar *reg)
{
    GIOChannel *io = g_io_channel_new_file(_jm_auth_dat_path->str, "w", NULL);
    gsize bytes_written = 0;

    if (io == NULL)
    {
        return;
    }

    g_io_channel_write_chars(io, reg, g_utf8_strlen(reg, -1), &bytes_written, NULL);
    g_io_channel_shutdown(io, TRUE, NULL);
    g_io_channel_unref(io);
}

static gchar** _jm_auth_decrypt(void)
{
    GIOChannel *io = NULL;
    gchar *file_text = NULL;
    gchar *cipher = NULL;
    gchar *recovered = NULL;
    gchar **result = NULL;
    gsize length = 0;
    int ret;
    int flen;

    io = g_io_channel_new_file(_jm_auth_dat_path->str, "r", NULL);

    if (io == NULL)
    {
        goto RETURN;
    }

    g_io_channel_read_to_end(io, &file_text, &length, NULL);

    if (length <= 0)
    {
        goto RETURN;
    }

    cipher = g_base64_decode(file_text, &length);

    if (cipher == NULL || length <= 0)
    {
        goto RETURN;
    }

    flen = RSA_size(_jm_auth_rsa);
    recovered = (gchar *)g_malloc(flen);
    memset(recovered, 0, flen);

    ret = RSA_private_decrypt(length, cipher, recovered, _jm_auth_rsa, RSA_PKCS1_OAEP_PADDING);
    if (ret < 0)
    {
        goto RETURN;
    }

    result = g_strsplit_set(recovered, "\n", 0);

    if (result == NULL)
        goto RETURN;

    length = g_strv_length(result);

    if (length != (CRYPT_MESSAGE_LINES + 1))
    {
        g_strfreev(result);
        result = NULL;
        goto RETURN;
    }

RETURN:
    // No need io channel here, free it
    if (io != NULL)
    {
        g_io_channel_shutdown(io, FALSE, NULL);
        g_io_channel_unref(io);
    }

    if (file_text != NULL)
    {
        g_free(file_text);
    }

    if (cipher != NULL)
    {
        g_free(cipher);
    }

    if (recovered != NULL)
    {
        g_free(recovered);
    }

    return result;
}

gchar* jm_auth_query_id_code(void)
{
    GString *id_code = NULL;
    GString *plain = g_string_new("");
    gchar *temp = NULL;

    size_t count = jm_disk_info_load();
    size_t i;

    for (i = 0; i < count; i++)
    {
        temp = jm_disk_info_serial_number(i);
        g_string_append_printf(plain, "%s\n", temp);
        g_free(temp);
    }

    count = cyoBase16EncodeGetLength(plain->len);
    id_code = g_string_sized_new(count);

    cyoBase16Encode(id_code->str, plain->str, plain->len);

    g_string_free(plain, TRUE);

    g_string_set_size(id_code, 16);

    return g_string_free(id_code, FALSE);
}

gchar* jm_auth_decrypt(size_t index)
{
    gchar ** vec = _jm_auth_decrypt();
    GString *str = NULL;

    if (vec == NULL)
    {
        return NULL;
    }

    str = g_string_new(vec[index]);

    g_strfreev(vec);

    return g_string_free(str, FALSE);
}

gboolean jm_auth_check_reg(void)
{
    gchar **vec = _jm_auth_decrypt();
    gboolean ret = vec != NULL ? FALSE : TRUE;
    g_strfreev(vec);
    return ret;
}
