// N: 
// E: 5847329
// D: 
// the encryption 64base text is:
// 1. ID code generate with Hardware SN \n
// 2. Commbox Customer ID CAEN/CACN/XXXXXXXXXXX \n
// 3. First Regisiter Time \n
// 4. Expired Update Time \n
// 5. The database password \n
// 6. The language \n
// Only the ID Code get from client computer;

static const char *theN = "";
static const char *theE = "520867428764322287956";
static const char *theD = "";
static const size_t DES_KEY_LENTH = 24;
static RSA *rsa = NULL;
static GString *datPath = NULL;

static gchar ** decrypt(void)
{
    FILE *in = NULL;
    gchar *fileText = NULL;
    long fileSize = 0;
    size_t result = 0;
    gchar *cipher = NULL;
    int flen = 0;
    gchar *recovered = NULL;
    
    in = fopen(datPath->str, "r");
    if (in == NULL)
        return NULL;
    
    // obtain file size
    fseek(in, 0, SEEK_END);
    fileSize = ftell(in);
    rewind(in);
    
    // allocate memory to contain the whole file:
    fileText = (gchar*)g_malloc(sizeof(gchar) * fileSize);
    
    // copy the file into the buffer
    result = fread(fileText, 1, fileSize, in);
    fclose(in);
    if (result != fileSize) {
        g_free(fileText);
        return NULL;
    }
    
    result = cyoBase64DecodeGetLength(result);
    cipher = (gchar*)g_malloc(sizeof(gchar) * result);
    result = cyoBase64Decode(cipher, fileText, fileSize);
    g_free(fileText);
    
    if (result <= 0) {
        g_free(cipher);
        return NULL;
    }
    
    flen = (int)RSA_size(rsa);
    recovered = (gchar*)g_malloc(sizeof(gchar) * flen);
    memset(recovered, 0, flen);
    
    flen = RSA_private_decrypt(result, 
		(const unsigned char*)cipher, 
		(unsigned char*)recovered, 
		rsa, 
		RSA_PKCS1_OAEP_PADDING);
    g_free(cipher);
    if (flen < 0) {
        g_free(recovered);
        return NULL;
    }
    
    return g_strsplit(recovered, "\n", flen);
}

void register_init(const gchar *path)
{
	GString *n = g_string_new("");
	GString *e = g_string_new("");
	GString *d = g_string_new("");

	long i;
	long length;

	length = strlen(theN);
	for (i = 0; i < length; i += 2)
	{
		n = g_string_append_c(n, theN[i]);
	}

	length = strlen(theE);
	for (i = 0; i < length; i+= 3)
	{
		e = g_string_append_c(e, theE[i]);
	}

	length = strlen(theD);
	for (i = 0; i < length; i += 4)
	{
		d = g_string_append_c(d, theD[i]);
	}

	rsa = RSA_new();

	rsa->n = BN_new();
	rsa->e = BN_new();
	rsa->d = BN_new();

	BN_dec2bn(&rsa->n, n->str);
	BN_dec2bn(&rsa->e, e->str);
	BN_dec2bn(&rsa->d, d->str);
	
	g_string_free(n, TRUE);
	g_string_free(e, TRUE);
	g_string_free(d, TRUE);

    disk_info_init();
    datPath = g_string_new(path);
	datPath = g_string_append(datPath, "demo.dat");
}

void register_dispose(void)
{
    disk_info_dispose();
    g_string_free(datPath, TRUE);
}

void register_save(const gchar *reg)
{
    FILE *out = fopen(datPath->str, "w");
    fwrite(reg, 1, strlen(reg) + 1, out);
    fclose(out);
}

gboolean register_query_id_code(gchar *id_code)
{
    gchar *result = NULL;
    GString *plain = NULL;
    gchar *temp = NULL;
    
    size_t count = disk_info_load();
    size_t i;
    
    plain = g_string_new("");
    for (i = 0; i < count; i++)
	{
        temp = disk_info_serial_number(i);
        plain = g_string_append(plain, temp);
        g_free(temp);
    }
    
    count = cyoBase16EncodeGetLength(plain->len);
    result = (gchar*)g_malloc(sizeof(gchar) * count);
    
    cyoBase16Encode(result, plain->str, plain->len);
    g_string_free(plain, TRUE);
    
    g_strlcpy(id_code, result, 16);
    g_free(result);
    return TRUE;
}

gboolean register_check(void)
{
    gchar **vec = decrypt();
    gchar *idCode1 = NULL;
    gchar idCode2[1024];
    gboolean ret;
    if (vec == NULL)
        return FALSE;
    
    if (g_strv_length(vec) < MessageLines)
	{
        g_strfreev(vec);
        return FALSE;
    }
    
    idCode1 = vec[IDCode];
    register_query_id_code(idCode2);
    
    ret = g_ascii_strcasecmp(idCode1, idCode2) == 0;
    g_strfreev(vec);
    return ret;
}

gboolean register_decrypt(DecryptType index, char *result)
{
    gchar **vec = decrypt();
    if (vec == NULL)
	{
        return FALSE;
    }
    
    if (index >= g_strv_length(vec))
	{
        g_strfreev(vec);
        return FALSE;
    }
    
    g_stpcpy(result, vec[index]);
    g_strfreev(vec);
    return TRUE;
}


